import React, { useState, useEffect } from 'react';

const AttendanceRecords = ({ branch, setBranch }) => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadAttendance = async () => {
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:5000/api/attendance/${branch}`);
      const data = await res.json();
      setRecords(data);
    } catch (error) {
      console.log('Attendance load failed:', error);
    }
    setLoading(false);
  };

  useEffect(() => {
    loadAttendance();
  }, [branch]);

  const branches = ['CSE', 'AIML', 'AIDS', 'ECE', 'MECH'];

  return (
    <div>
      {/* Branch Tabs */}
      <div className="branch-tabs">
        {branches.map(b => (
          <button
            key={b}
            className={`tab-btn ${branch === b ? 'active' : ''}`}
            onClick={() => setBranch(b)}
          >
            {b}
          </button>
        ))}
      </div>

      {/* Stats Header */}
      <div className="stats-header">
        <div className="stat-box">
          <div className="stat-number">{records.length}</div>
          <div className="stat-label">Today's Records</div>
        </div>
        <div className="stat-box">
          <div className="stat-number present-count">
            {records.filter(r => r.status === 'Present').length}
          </div>
          <div className="stat-label">Present</div>
        </div>
      </div>

      {/* Records List */}
      <div className="records-container">
        {loading ? (
          <div className="loading">ðŸ”„ Loading attendance records...</div>
        ) : records.length === 0 ? (
          <div className="empty-state">
            <i className="fas fa-clipboard-list"></i>
            <h3>No attendance records</h3>
            <p>Mark attendance to see records here</p>
          </div>
        ) : (
          records.map((record, index) => (
            <div key={index} className="attendance-item">
              <div className="record-left">
                <div className="record-avatar">
                  {record.name.charAt(0).toUpperCase()}
                </div>
                <div className="record-info">
                  <h4>{record.name}</h4>
                  <p>ID: {record.admission_no}</p>
                  <p className="record-time">{record.time}</p>
                </div>
              </div>
              <div className="record-right">
                <span className={`status-badge status-${record.status.toLowerCase()}`}>
                  {record.status}
                </span>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AttendanceRecords;
