// src/components/MarkAttendance.js
import React, { useState, useRef, useEffect } from 'react';
import { useEffectOnce } from 'react-use'; // optional, or use custom hook below

const API_BASE = 'http://localhost:5000';

const MarkAttendance = ({ onStatsUpdate }) => {
  const [status, setStatus] = useState('starting');
  const [result, setResult] = useState(null);
  const [isProcessing, setIsProcessing] = useState(true);
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const timeoutRef = useRef(null);

  // Custom hook to run only once (like useEffectOnce)
  const useEffectOnce = (effect) => {
    useEffect(effect, []);
  };

  // AUTO START camera when component mounts (when "Mark Attendance" tab is clicked)
  useEffectOnce(() => {
    startCameraAndDetect();
  });

  const startCameraAndDetect = async () => {
    setStatus('starting');
    
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 320, height: 240 }
      });
      streamRef.current = stream;
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
      }

      setStatus('detecting');
      
      // Auto capture after 800ms
      timeoutRef.current = setTimeout(captureAndProcess, 800);
      
    } catch (err) {
      console.error(err);
      setStatus('error');
      setResult('Camera access denied');
      setIsProcessing(false);
    }
  };

  const captureAndProcess = async () => {
    const imageDataUrl = captureFrameAsDataURL();
    if (!imageDataUrl) {
      setStatus('error');
      setResult('Failed to capture frame');
      stopCamera();
      return;
    }

    try {
      setStatus('processing');
      const res = await fetch(`${API_BASE}/api/mark_attendance`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ frame: imageDataUrl })
      });
      
      const data = await res.json();
      
      if (data.students && data.students.length > 0) {
        setStatus('success');
        setResult(data.students);
        if (onStatsUpdate) onStatsUpdate();
      } else {
        setStatus('no_face');
        setResult(data.error || 'No known faces detected');
      }
    } catch (err) {
      console.error(err);
      setStatus('error');
      setResult('Network error');
    } finally {
      stopCamera();
    }
  };

  const captureFrameAsDataURL = () => {
    if (!videoRef.current) return null;
    const video = videoRef.current;
    const canvas = document.createElement('canvas');
    canvas.width = video.videoWidth || 320;
    canvas.height = video.videoHeight || 240;
    const ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    return canvas.toDataURL('image/jpeg');
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setIsProcessing(false);
  };

  // Auto restart after 3 seconds if no face
  useEffect(() => {
    if (status === 'no_face') {
      const timer = setTimeout(() => {
        setStatus('ready');
        setResult(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [status]);

  // Cleanup
  useEffect(() => {
    return () => stopCamera();
  }, []);

  return (
    <div className="mark-attendance-container">
      <div className="attendance-header">
        <h3>üé• Auto Mark Attendance</h3>
        <p>Camera detecting automatically...</p>
      </div>

      {(status === 'starting' || status === 'detecting' || status === 'processing') && (
        <div className="camera-auto-container">
          <video 
            ref={videoRef} 
            autoPlay
            className="camera-auto-video"
          />
          <div className="detection-overlay">
            <div className={`status-indicator ${status}`}>
              <i className={`fas ${status === 'starting' ? 'fa-spinner fa-spin' : 
                                   status === 'detecting' ? 'fa-search' : 'fa-brain'}`} />
              <span>
                {status === 'starting' && 'üîÑ Starting camera...'}
                {status === 'detecting' && 'üîç Detecting face...'}
                {status === 'processing' && '‚ö° Processing...'}
              </span>
            </div>
          </div>
        </div>
      )}

      {status === 'success' && result && (
        <div className="success-result">
          <i className="fas fa-check-circle fa-3x"></i>
          <h3>‚úÖ Attendance Marked Successfully!</h3>
          <div className="students-list">
            {result.map((student, idx) => (
              <div key={idx} className="student-marked">
                <span className="student-name">{student.name}</span>
                <span className="confidence">{(student.confidence * 100).toFixed(1)}%</span>
              </div>
            ))}
          </div>
          <button className="retry-btn" onClick={startCameraAndDetect}>
            üì∏ Mark Another
          </button>
        </div>
      )}

      {(status === 'no_face' || status === 'error') && result && (
        <div className={`error-result ${status === 'no_face' ? 'no-face' : ''}`}>
          <i className={`fas fa-${status === 'no_face' ? 'user-slash' : 'exclamation-triangle'} fa-3x`}></i>
          <h3>{status === 'no_face' ? 'üë§ No Face Detected' : '‚ùå Detection Failed'}</h3>
          <p>{result}</p>
          <button className="retry-btn" onClick={startCameraAndDetect}>
            üîÑ Try Again
          </button>
        </div>
      )}
    </div>
  );
};

export default MarkAttendance;
