// src/components/AttendanceRecords.js
import React, { useState, useEffect } from 'react';

const AttendanceRecords = ({ branch, onStatsUpdate }) => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchRecords();
  }, [branch]);

  const fetchRecords = async () => {
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:5000/api/attendance/${branch}`);
      const data = await res.json();
      setRecords(data);
    } catch (error) {
      console.error('Error fetching records:', error);
    }
    setLoading(false);
    if (onStatsUpdate) onStatsUpdate();
  };

  if (loading) {
    return (
      <div className="attendance-empty">
        <i className="fas fa-spinner fa-spin fa-2x"></i>
        <p>Loading attendance...</p>
      </div>
    );
  }

  return (
    <div className="attendance-records-container">
      <div className="records-header">
        <h3>ðŸ“‹ Today's Attendance Records</h3>
        <p>{records.length} students marked present today</p>
      </div>

      {records.length === 0 ? (
        <div className="no-records">
          <i className="fas fa-calendar-times fa-3x"></i>
          <h3>No attendance records today</h3>
          <p>Mark attendance using the camera!</p>
        </div>
      ) : (
        <div className="records-grid">
          {records.map((record, index) => (
            <div key={index} className="attendance-card">
              <div className="card-header">
                <div className="status-badge present">
                  <i className="fas fa-check-circle"></i>
                  <span>PRESENT</span>
                </div>
              </div>
              
              <div className="card-body">
                <div className="admission-no">{record.admission_no}</div>
                <div className="student-name">{record.name}</div>
                <div className="branch-tag">{record.branch}</div>
              </div>
              
              <div className="card-footer">
                <span className="time-stamp">{record.time}</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AttendanceRecords;
