// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import './App.css';

// Components
import Dashboard from './components/Dashboard';
import AdminPanel from './components/AdminPanel';
import ManageStudents from './components/ManageStudents';
import MarkAttendance from './components/MarkAttendance';
import SearchStudent from './components/SearchStudent';
import AttendanceRecords from './components/AttendanceRecords';

const Sidebar = ({ stats, activeTab, setActiveTab }) => {
  const navigate = useNavigate();
  const branches = ['AIML', 'CSE', 'ECE', 'MECH', 'CIVIL'];

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h1>ðŸ§  Smart Attendance</h1>
        <div className="stats-card">
          <div>
            <div className="stats-card-title">Total Students</div>
            <div className="stats-card-value">{stats.total}</div>
          </div>
          <div className="stats-card-value">{stats.today_present}</div>
        </div>
        <div className="stats-card">
          <div>
            <div className="stats-card-title">Today Present</div>
            <div className="stats-card-value">{stats.today_present}</div>
          </div>
          <div className="stats-card-value">{stats.today_date}</div>
        </div>
      </div>

      <div className="sidebar-menu">
        <button 
          className={`sidebar-button ${activeTab === 'dashboard' ? 'active' : ''}`}
          onClick={() => { setActiveTab('dashboard'); navigate('/'); }}
        >
          <i className="fas fa-home"></i>
          Dashboard
        </button>
        <button 
          className={`sidebar-button ${activeTab === 'admin' ? 'active' : ''}`}
          onClick={() => { setActiveTab('admin'); navigate('/admin'); }}
        >
          <i className="fas fa-user-tie"></i>
          Admin
        </button>
        {branches.map(branch => (
          <div key={branch}>
            <button 
              className={`sidebar-button ${activeTab === `manage-${branch.toLowerCase()}` ? 'active' : ''}`}
              onClick={() => { setActiveTab(`manage-${branch.toLowerCase()}`); navigate(`/manage-students/${branch}`); }}
            >
              <i className="fas fa-users"></i>
              {branch}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

const MainContent = ({ stats, activeTab, setActiveTab }) => {
  const location = useLocation();
  const [branch, setBranch] = useState('AIML');

  const updateStats = () => {
    // Trigger stats refresh
  };

  return (
    <div className="main-content">
      <div className="header">
        <h2>Smart Attendance System</h2>
        <button className="admin-btn" onClick={() => setActiveTab('admin')}>
          <i className="fas fa-user-tie"></i>
          Admin Panel
        </button>
      </div>

      <div className="branch-tabs">
        {['AIML', 'CSE', 'ECE', 'MECH', 'CIVIL'].map(b => (
          <button 
            key={b}
            className={`tab-btn ${branch === b ? 'active' : ''}`}
            onClick={() => setBranch(b)}
          >
            {b}
          </button>
        ))}
      </div>

      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/admin" element={<AdminPanel />} />
        <Route path="/manage-students/:branch" element={<ManageStudents branch={branch} onStatsUpdate={updateStats} />} />
        <Route path="/mark-attendance" element={<MarkAttendance branch={branch} onStatsUpdate={updateStats} />} />
        <Route path="/search-student" element={<SearchStudent branch={branch} />} />
        <Route path="/attendance-records" element={<AttendanceRecords branch={branch} onStatsUpdate={updateStats} />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </div>
  );
};

function AppContent() {
  const [stats, setStats] = useState({ total: 0, today_present: 0, today_date: '' });
  const [activeTab, setActiveTab] = useState('dashboard');

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/stats');
        const data = await res.json();
        setStats(data);
      } catch (error) {
        console.error('Error fetching stats:', error);
      }
    };

    fetchStats();
    const interval = setInterval(fetchStats, 30000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="app">
      <Sidebar stats={stats} activeTab={activeTab} setActiveTab={setActiveTab} />
      <MainContent stats={stats} activeTab={activeTab} setActiveTab={setActiveTab} />
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
