import React, { useState, useEffect, useRef, useCallback } from 'react';

const API_BASE = 'http://localhost:5000';

const ManageStudents = ({ branch, setBranch }) => {
  const [students, setStudents] = useState([]);
  const [formData, setFormData] = useState({
    admissionNo: '',
    name: '',
    specialization: '',
    email: '',
    semester: '1',
  });

  const [enrollingStudent, setEnrollingStudent] = useState(null);
  const [isEnrolling, setIsEnrolling] = useState(false);
  const [isMarkingAttendance, setIsMarkingAttendance] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // ‚úÖ LOAD ONLY MANAGE STUDENTS
  const loadStudents = useCallback(async () => {
    try {
      const res = await fetch(`${API_BASE}/api/students/${branch}`);
      const data = await res.json();
      setStudents(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Load students failed', err);
    }
  }, [branch]);

  useEffect(() => {
    loadStudents();
  }, [loadStudents]);

  // ‚ûï ADD STUDENT (MANAGE ONLY)
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`${API_BASE}/api/add_student`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          admission_no: formData.admissionNo.trim(),
          name: formData.name.trim(),
          branch,
          specialization: formData.specialization.trim(),
          email: formData.email.trim(),
          semester: formData.semester,
        }),
      });

      const data = await res.json();

      if (res.ok && data.success) {
        alert('‚úÖ Student added successfully');
        setFormData({
          admissionNo: '',
          name: '',
          specialization: '',
          email: '',
          semester: '1',
        });
        loadStudents(); // ‚úÖ ONLY REFRESH MANAGE LIST
      } else {
        alert(data.error || 'Add failed');
      }
    } catch (err) {
      alert('Network error');
    }
  };

  // üóëÔ∏è DELETE (MANAGE ONLY)
  const deleteStudent = async (admissionNo) => {
    if (!window.confirm('Delete this student?')) return;

    try {
      const res = await fetch(`${API_BASE}/api/delete_student`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ admission_no: admissionNo }),
      });

      const data = await res.json();

      if (res.ok && data.success) {
        alert('‚úÖ Student deleted');
        loadStudents(); // ‚úÖ ONLY MANAGE REFRESH
      } else {
        alert(data.error || 'Delete failed');
      }
    } catch (err) {
      alert('Delete error');
    }
  };

  // üì∏ CAMERA
  const startCamera = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    streamRef.current = stream;
    videoRef.current.srcObject = stream;
    await videoRef.current.play();
  };

  const stopCamera = () => {
    streamRef.current?.getTracks().forEach(t => t.stop());
    videoRef.current.srcObject = null;
  };

  const enrollFace = async () => {
    const canvas = document.createElement('canvas');
    canvas.width = 320;
    canvas.height = 240;
    canvas.getContext('2d').drawImage(videoRef.current, 0, 0);
    const image = canvas.toDataURL('image/jpeg');

    try {
      const res = await fetch(`${API_BASE}/api/enroll_face`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          admission_no: enrollingStudent.admission_no,
          name: enrollingStudent.name,
          image,
        }),
      });

      const data = await res.json();

      if (res.ok && data.success) {
        alert('‚úÖ Face enrolled');
        loadStudents();
      } else {
        alert(data.error || 'Enroll failed');
      }
    } finally {
      setIsEnrolling(false);
      stopCamera();
    }
  };

  const branches = ['CSE', 'AIML', 'AIDS', 'ECE', 'MECH'];

  return (
    <div>
      <div className="branch-tabs">
        {branches.map(b => (
          <button
            key={b}
            className={`tab-btn ${branch === b ? 'active' : ''}`}
            onClick={() => setBranch(b)}
          >
            {b}
          </button>
        ))}
      </div>

      <form className="form-grid" onSubmit={handleSubmit}>
        <input placeholder="Admission No" value={formData.admissionNo}
          onChange={e => setFormData({ ...formData, admissionNo: e.target.value })} required />
        <input placeholder="Name" value={formData.name}
          onChange={e => setFormData({ ...formData, name: e.target.value })} required />
        <input placeholder="Specialization" value={formData.specialization}
          onChange={e => setFormData({ ...formData, specialization: e.target.value })} />
        <input placeholder="Email" value={formData.email}
          onChange={e => setFormData({ ...formData, email: e.target.value })} />
        <select value={formData.semester}
          onChange={e => setFormData({ ...formData, semester: e.target.value })}>
          {[1,2,3,4,5,6,7,8].map(s => <option key={s}>{s}</option>)}
        </select>
        <button type="submit">‚ûï Add Student</button>
      </form>

      {students.map(s => (
        <div key={s.admission_no} className="student-item">
          <h3>{s.name}</h3>
          <p>{s.admission_no} ¬∑ {branch}</p>
          <button onClick={() => deleteStudent(s.admission_no)}>üóëÔ∏è Delete</button>
        </div>
      ))}

      {isEnrolling && (
        <div>
          <video ref={videoRef} autoPlay />
          <button onClick={enrollFace}>üì∏ Capture & Enroll</button>
        </div>
      )}
    </div>
  );
};

export default ManageStudents;
