import { useState } from "react";

const API_BASE = "http://localhost:5000";

export default function SearchStudent() {
  const [searchId, setSearchId] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (!searchId.trim()) return;

    setLoading(true);
    setResult(null); // üî• clear old result

    const res = await fetch(`${API_BASE}/api/search_student`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ search_term: searchId }),
    });

    const data = await res.json();

    if (!data.success) {
      setResult({ success: false });
    } else {
      setResult(data);
    }

    setLoading(false);
  };

  return (
    <div>
      <h2>Search Student</h2>

      <input
        placeholder="Admission No"
        value={searchId}
        onChange={(e) => setSearchId(e.target.value)}
      />

      <button onClick={handleSearch}>
        {loading ? "Searching..." : "Search"}
      </button>

      {/* ‚úÖ SUCCESS */}
      {result?.success && (
        <div className="card">
          <h3>{result.student.name}</h3>
          <p>{result.student.admission_no}</p>
          <p>{result.student.branch}</p>
          <p>{result.student.phone}</p>
        </div>
      )}

      {/* ‚ùå NOT FOUND */}
      {result?.success === false && (
        <p style={{ color: "red" }}>Search student not found</p>
      )}
    </div>
  );
}
