// src/components/AttendanceRecords.js
import React, { useState, useEffect } from 'react';

const API_BASE = 'http://localhost:5000';

const AttendanceRecords = ({ branch }) => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadTodayAttendance = async () => {
    try {
      setLoading(true);
      console.log('ðŸ“‹ Loading attendance for:', branch);
      
      const res = await fetch(`${API_BASE}/api/today_attendance/${branch}`);
      const data = await res.json();
      console.log('ðŸ“‹ Records loaded:', data);
      
      setRecords(data || []);
    } catch (error) {
      console.error('Load attendance failed:', error);
      setRecords([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadTodayAttendance();
  }, [branch]);

  // âœ… AUTO REFRESH every 10 seconds
  useEffect(() => {
    const interval = setInterval(loadTodayAttendance, 10000);
    return () => clearInterval(interval);
  }, [branch]);

  if (loading) {
    return (
      <div className="attendance-empty">
        <i className="fas fa-spinner fa-spin fa-2x"></i>
        <p>Loading attendance records...</p>
      </div>
    );
  }

  return (
    <div className="attendance-records">
      <div className="records-header">
        <h3>ðŸ“‹ Today's Attendance - {branch}</h3>
        <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
          <span className="record-count">{records.length} marked</span>
          <button className="refresh-btn" onClick={loadTodayAttendance} title="Refresh records">
            <i className="fas fa-sync-alt"></i> Refresh
          </button>
        </div>
      </div>

      {records.length === 0 ? (
        <div className="attendance-empty">
          <i className="fas fa-users-slash fa-3x"></i>
          <h3>No attendance records today</h3>
          <p>Mark some attendance first to see records here</p>
          <button 
            className="refresh-btn" 
            onClick={loadTodayAttendance}
            style={{ marginTop: '1rem', width: '200px' }}
          >
            ðŸ”„ Check for Updates
          </button>
        </div>
      ) : (
        <div className="records-list">
          {records.map((record, idx) => (
            <div key={idx} className="attendance-record">
              <div className="record-info">
                <h4>{record.name}</h4>
                <p>ID: {record.admission_no}</p>
                <p>Time: {new Date(record.timestamp).toLocaleTimeString()}</p>
              </div>
              <div className="record-status">
                <span className="status-present">âœ… Present</span>
                <span className="confidence">{record.confidence}%</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AttendanceRecords;
