// src/components/AttendanceRecords.js
import React, { useState, useEffect } from 'react';

const API_BASE = 'http://localhost:5000';

const AttendanceRecords = ({ branch = 'CSE' }) => {
  const [todayRecords, setTodayRecords] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadTodayRecords = async () => {
    try {
      setLoading(true);
      const res = await fetch(`${API_BASE}/api/today_attendance/${branch}`);
      const data = await res.json();
      setTodayRecords(data);
    } catch (error) {
      console.error('Load records failed:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadTodayRecords();
    const interval = setInterval(loadTodayRecords, 5000); // Refresh every 5s
    return () => clearInterval(interval);
  }, [branch]);

  if (loading) {
    return (
      <div className="attendance-records-container">
        <div className="page-title">ðŸ“‹ Loading Records...</div>
      </div>
    );
  }

  return (
    <div className="attendance-records-container">
      <div className="records-header">
        <h2 className="page-title">ðŸ“‹ Today's Attendance Records</h2>
        <div className="stats-row">
          <div className="stat-box">
            <span className="stat-label">Total Marks</span>
            <span className="stat-value">{todayRecords.length}</span>
          </div>
          <div className="stat-box">
            <span className="stat-label">Unique Students</span>
            <span className="stat-value">{new Set(todayRecords.map(r => r.admission_no)).size}</span>
          </div>
        </div>
      </div>

      <div className="records-grid">
        {todayRecords.length === 0 ? (
          <div className="empty-records">
            <div className="empty-icon">ðŸ“­</div>
            <h3>No attendance marked today</h3>
            <p>Attendance will appear here when students mark in</p>
          </div>
        ) : (
          todayRecords.map((record, index) => (
            <div key={`${record.admission_no}-${record.timestamp}`} className="attendance-box">
              <div className="box-avatar">{record.name.charAt(0)}</div>
              <div className="box-info">
                <div className="student-name">{record.name}</div>
                <div className="admission-branch">
                  <span className="admission-no">{record.admission_no}</span>
                  <span className="branch-badge">{record.branch}</span>
                </div>
              </div>
              <div className="box-status">
                <span className="status present">âœ… Present</span>
                <span className="time-stamp">{formatTime(record.timestamp)}</span>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Format timestamp to HH:MM:SS
const formatTime = (timestamp) => {
  const date = new Date(timestamp);
  return date.toLocaleTimeString('en-US', { 
    hour12: false, 
    hour: '2-digit', 
    minute: '2-digit', 
    second: '2-digit' 
  });
};

export default AttendanceRecords;
