import React, { useState } from 'react';

const API_BASE = 'http://localhost:5000';

const SearchStudent = () => {
  const [admissionNo, setAdmissionNo] = useState('');
  const [name, setName] = useState('');
  const [branch, setBranch] = useState('CSE');
  const [specialization, setSpecialization] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [dob, setDob] = useState('');
  const [photoFile, setPhotoFile] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);

  const [searchId, setSearchId] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleAddStudent = async (e) => {
    e.preventDefault();
    if (loading) return;

    setLoading(true);

    const fd = new FormData();
    fd.append('admission_no', admissionNo);
    fd.append('name', name);
    fd.append('branch', branch);
    fd.append('specialization', specialization);
    fd.append('email', email);
    fd.append('semester', '1');
    if (photoFile) {
      fd.append('photo', photoFile);
    }

    try {
      console.log('üì§ Adding student:', admissionNo, name);
      const res = await fetch(`${API_BASE}/api/add_student`, {
        method: 'POST',
        body: fd
      });
      
      const data = await res.json();
      console.log('üì• Add response:', data);
      
      if (res.ok && data.success) {
        window.alert(`‚úÖ ${name} added to students database!`);
        setAdmissionNo('');
        setName('');
        setBranch('CSE');
        setSpecialization('');
        setPhone('');
        setEmail('');
        setDob('');
        setPhotoFile(null);
        setPhotoPreview(null);
      } else {
        window.alert(data.error || 'Error adding student');
      }
    } catch (err) {
      console.error('‚ùå Add error:', err);
      window.alert('Network error - Check backend server');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchId.trim() || loading) return;

    setLoading(true);

    try {
      console.log('üîç Searching:', searchId.trim());
      const res = await fetch(`${API_BASE}/api/search_student`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          search_term: searchId.trim()
        })
      });
      
      const data = await res.json();
      console.log('üì• Search response:', data);
      
      setSearchResult(data);
      
    } catch (err) {
      console.error('‚ùå Search error:', err);
      setSearchResult({ success: false, error: 'Search failed' });
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ NEW: Delete Student Button
  const handleDeleteStudent = async () => {
    if (!searchResult?.student?.admission_no) return;
    
    if (!window.confirm(`Delete ${searchResult.student.name}?`)) return;

    try {
      setLoading(true);
      const res = await fetch(`${API_BASE}/api/delete_student`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          admission_no: searchResult.student.admission_no
        })
      });
      
      const data = await res.json();
      
      if (res.ok && data.success) {
        window.alert('‚úÖ Student deleted successfully!');
        setSearchResult(null);
        setSearchId('');
      } else {
        window.alert(data.error || 'Delete failed');
      }
    } catch (err) {
      console.error('‚ùå Delete error:', err);
      window.alert('Delete error - Check backend');
    } finally {
      setLoading(false);
    }
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setPhotoFile(file);
    if (file) {
      const url = URL.createObjectURL(file);
      setPhotoPreview(url);
    } else {
      setPhotoPreview(null);
    }
  };

  const getPhotoUrl = (photoPath) => {
    if (!photoPath) return null;
    const parts = photoPath.split(/[\\/]/);
    const filename = parts[parts.length - 1];
    return `${API_BASE}/static_uploads/${filename}`;
  };

  return (
    <div className="search-student-container">
      {/* Search box */}
      <div className="search-box">
        <input
          className="form-input search-input"
          placeholder="Enter Admission No to search..."
          value={searchId}
          onChange={(e) => setSearchId(e.target.value)}
          disabled={loading}
        />
        <button 
          className="search-btn" 
          onClick={handleSearch}
          disabled={loading}
        >
          {loading ? 'üîç Searching...' : 'üîç Search'}
        </button>
      </div>

      {/* Search result - FIXED ALIGNMENT */}
      {searchResult && searchResult.success && searchResult.student && (
        <div className="search-result-card">
          <div className="photo-wrapper">
            {searchResult.student.photo ? (
              <img
                src={getPhotoUrl(searchResult.student.photo)}
                alt={searchResult.student.name}
                className="student-photo"
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
              />
            ) : (
              <div className="student-photo placeholder">
                {searchResult.student.name.charAt(0).toUpperCase()}
              </div>
            )}
          </div>

          <div className="result-header">
            <div>
              <h3>{searchResult.student.name}</h3>
              <p>
                <strong>{searchResult.student.admission_no}</strong> ¬∑ {searchResult.student.branch}
              </p>
            </div>
          </div>

          {/* ‚úÖ FIXED: Perfectly aligned detail rows */}
          <div className="detail-grid">
            <div className="detail-row">
              <span className="detail-label">Specialization</span>
              <span className="detail-value">{searchResult.student.specialization || '-'}</span>
            </div>
            <div className="detail-row">
              <span className="detail-label">Semester</span>
              <span className="detail-value">{searchResult.student.semester || '-'}</span>
            </div>
            <div className="detail-row">
              <span className="detail-label">Email</span>
              <span className="detail-value">{searchResult.student.email || '-'}</span>
            </div>
            {searchResult.student.phone && (
              <div className="detail-row">
                <span className="detail-label">Phone</span>
                <span className="detail-value">{searchResult.student.phone}</span>
              </div>
            )}
            {searchResult.student.dob && (
              <div className="detail-row">
                <span className="detail-label">DOB</span>
                <span className="detail-value">{searchResult.student.dob}</span>
              </div>
            )}
          </div>

          {/* ‚úÖ NEW: Delete Button */}
          <div className="delete-actions">
            <button 
              className="delete-btn"
              onClick={handleDeleteStudent}
              disabled={loading}
            >
              üóëÔ∏è Delete Student
            </button>
          </div>
        </div>
      )}

      {searchResult && !searchResult.success && (
        <div className="empty-state">
          <i className="fas fa-search"></i>
          <p>{searchResult.error || 'No student found'}</p>
        </div>
      )}

      {/* Add student form */}
      <form className="form-grid" onSubmit={handleAddStudent}>
        <input
          className="form-input"
          placeholder="Admission No *"
          value={admissionNo}
          onChange={(e) => setAdmissionNo(e.target.value)}
          required
          disabled={loading}
        />
        <input
          className="form-input"
          placeholder="Full Name *"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          disabled={loading}
        />
        <select
          className="form-select"
          value={branch}
          onChange={(e) => setBranch(e.target.value)}
          disabled={loading}
        >
          <option value="CSE">CSE</option>
          <option value="AIML">AIML</option>
          <option value="AIDS">AIDS</option>
          <option value="ECE">ECE</option>
          <option value="MECH">MECH</option>
        </select>
        <input
          className="form-input"
          placeholder="Specialization"
          value={specialization}
          onChange={(e) => setSpecialization(e.target.value)}
          disabled={loading}
        />
        <input
          className="form-input"
          placeholder="Phone (optional)"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          disabled={loading}
        />
        <input
          className="form-input"
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          disabled={loading}
        />
        <input
          className="form-input"
          type="date"
          placeholder="Date of Birth"
          value={dob}
          onChange={(e) => setDob(e.target.value)}
          disabled={loading}
        />

        <div className="file-upload">
          <label className="file-upload-label">
            üì∏ Upload Photo (optional)
            <input
              type="file"
              accept="image/*"
              onChange={handlePhotoChange}
              disabled={loading}
            />
          </label>
          {photoPreview && (
            <img src={photoPreview} alt="Preview" className="file-preview" />
          )}
        </div>

        <button 
          type="submit" 
          className="form-btn"
          disabled={loading || !admissionNo || !name}
        >
          {loading ? '‚è≥ Adding...' : '‚ûï Add Student'}
        </button>
      </form>
    </div>
  );
};

export default SearchStudent;
