import { useEffect, useRef, useCallback } from 'react';

const MarkAttendance = ({ branch }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // ‚úÖ WEBCAM START
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: { width: 320, height: 240, facingMode: 'user' }
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          streamRef.current = stream;
        }
      } catch (err) {
        console.error('Camera error:', err);
        alert('‚ùå Camera access denied/blocked!');
      }
    };
    startCamera();

    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  const markAttendance = useCallback(async () => {
    if (!videoRef.current || !canvasRef.current) {
      alert('‚ùå Webcam not ready! Refresh page.');
      return;
    }

    console.log('üì∏ Capturing...');
    
    const video = videoRef.current;
    const canvas = canvasRef.current;
    canvas.width = 320;
    canvas.height = 240;
    const ctx = canvas.getContext('2d');
    
    ctx.drawImage(video, 0, 0, 320, 240);
    const imageDataUrl = canvas.toDataURL('image/jpeg', 0.8);
    
    console.log('‚úÖ Image ready:', imageDataUrl.length > 100); // DEBUG

    try {
      const res = await fetch(`${API_BASE}/api/mark_attendance`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          image: imageDataUrl,
          branch: branch
        })
      });

      const data = await res.json();
      console.log('üìÑ Backend response:', data);

      if (res.ok && data.success) {
        alert(`‚úÖ ${data.message}\nConfidence: ${data.confidence}`);
      } else {
        alert(`‚ùå ${data.error}`);
      }
    } catch (error) {
      alert('‚ùå Backend not running!');
    }
  }, [branch]);

  return (
    <div className="attendance-card">
      <h3>üì∏ Mark Attendance - {branch}</h3>
      
      <div className="webcam-container">
        <video 
          ref={videoRef} 
          autoPlay 
          muted 
          playsInline 
          style={{ width: '320px', height: '240px', border: '2px solid #007bff' }}
        />
        <canvas ref={canvasRef} style={{ display: 'none' }} />
      </div>
      
      <button onClick={markAttendance} className="btn-primary">
        ‚úÖ MARK ATTENDANCE
      </button>
    </div>
  );
};
