import React, { useState } from 'react';

const API_BASE = 'http://localhost:5000';

const SearchStudent = () => {
  const [admissionNo, setAdmissionNo] = useState('');
  const [name, setName] = useState('');
  const [branch, setBranch] = useState('CSE');
  const [specialization, setSpecialization] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [dob, setDob] = useState('');
  const [photoFile, setPhotoFile] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);

  const [searchId, setSearchId] = useState('');
  const [searchResult, setSearchResult] = useState(null);

  const handleAddSearchStudent = async (e) => {
    e.preventDefault();

    const fd = new FormData();
    fd.append('admission_no', admissionNo);
    fd.append('name', name);
    fd.append('branch', branch);
    fd.append('specialization', specialization);
    fd.append('phone', phone);
    fd.append('email', email);
    fd.append('dob', dob);
    if (photoFile) {
      fd.append('photo', photoFile);
    }

    try {
      const res = await fetch(`${API_BASE}/api/search_student`, {
        method: 'POST',
        body: fd
      });
      const data = await res.json();
      if (res.ok && data.success) {
        window.alert('Student added to search database ✅');
        setAdmissionNo('');
        setName('');
        setBranch('CSE');
        setSpecialization('');
        setPhone('');
        setEmail('');
        setDob('');
        setPhotoFile(null);
        setPhotoPreview(null);
      } else {
        window.alert(data.error || 'Error adding search student');
        console.error('Search add error:', data);
      }
    } catch (err) {
      console.error(err);
      window.alert('Error adding search student');
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchId.trim()) return;

    try {
      const res = await fetch(
        `${API_BASE}/api/search_student/${searchId.trim()}`
      );
      const data = await res.json();
      setSearchResult(data);
    } catch (err) {
      console.error(err);
      window.alert('Error searching student');
    }
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setPhotoFile(file);
    if (file) {
      const url = URL.createObjectURL(file);
      setPhotoPreview(url);
    } else {
      setPhotoPreview(null);
    }
  };

  const getPhotoUrl = (photoPath) => {
    if (!photoPath) return null;
    // photoPath stored like "static_uploads/E220160_photo.jpg"
    const parts = photoPath.split(/[\\/]/);
    const filename = parts[parts.length - 1];
    return `${API_BASE}/static_uploads/${filename}`;
  };

  return (
    <div>
      {/* Search box */}
      <div className="search-box">
        <input
          className="form-input search-input"
          placeholder="Enter Admission No to search..."
          value={searchId}
          onChange={(e) => setSearchId(e.target.value)}
        />
        <button className="search-btn" onClick={handleSearch}>
          Search
        </button>
      </div>

      {/* Search result card with photo */}
      {searchResult && searchResult.found && (
        <div className="search-result-card">
          <div className="photo-wrapper">
            {getPhotoUrl(searchResult.photo) ? (
              <img
                src={getPhotoUrl(searchResult.photo)}
                alt={searchResult.name}
                className="student-photo"
              />
            ) : (
              <div className="student-photo placeholder">
                {searchResult.name ? searchResult.name.charAt(0) : '?'}
              </div>
            )}
          </div>

          <div className="result-header">
            <div>
              <h3>{searchResult.name}</h3>
              <p>
                {searchResult.admission_no} · {searchResult.branch}
              </p>
            </div>
          </div>

          <div className="detail-row">
            <span>Specialization</span>
            <span>{searchResult.specialization || '-'}</span>
          </div>
          <div className="detail-row">
            <span>Phone</span>
            <span>{searchResult.phone || '-'}</span>
          </div>
          <div className="detail-row">
            <span>Email</span>
            <span>{searchResult.email || '-'}</span>
          </div>
          <div className="detail-row">
            <span>Date of Birth</span>
            <span>{searchResult.dob || '-'}</span>
          </div>
        </div>
      )}

      {searchResult && !searchResult.found && (
        <div className="empty-state">
          <i className="fas fa-search"></i>
          <p>No student found with this admission number.</p>
        </div>
      )}

      {/* Add search student form */}
      <form className="form-grid" onSubmit={handleAddSearchStudent}>
        <input
          className="form-input"
          placeholder="Admission No *"
          value={admissionNo}
          onChange={(e) => setAdmissionNo(e.target.value)}
          required
        />
        <input
          className="form-input"
          placeholder="Full Name *"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <select
          className="form-select"
          value={branch}
          onChange={(e) => setBranch(e.target.value)}
        >
          <option value="CSE">CSE</option>
          <option value="AIML">AIML</option>
          <option value="AIDS">AIDS</option>
          <option value="ECE">ECE</option>
          <option value="MECH">MECH</option>
        </select>
        <input
          className="form-input"
          placeholder="Specialization"
          value={specialization}
          onChange={(e) => setSpecialization(e.target.value)}
        />
        <input
          className="form-input"
          placeholder="Phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
        <input
          className="form-input"
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          className="form-input"
          type="date"
          placeholder="Date of Birth"
          value={dob}
          onChange={(e) => setDob(e.target.value)}
        />

        <div className="file-upload">
          <label className="file-upload-label">
            Upload Photo
            <input
              type="file"
              accept="image/*"
              onChange={handlePhotoChange}
            />
          </label>
          {photoPreview && (
            <img src={photoPreview} alt="Preview" className="file-preview" />
          )}
        </div>

        <button type="submit" className="form-btn">
          ➕ Add to Search
        </button>
      </form>
    </div>
  );
};

export default SearchStudent;
