import React, { useState } from 'react';

const API_BASE = 'http://localhost:5000';

const SearchStudent = () => {
  const [admissionNo, setAdmissionNo] = useState('');
  const [name, setName] = useState('');
  const [branch, setBranch] = useState('CSE');
  const [specialization, setSpecialization] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [dob, setDob] = useState('');
  const [photoFile, setPhotoFile] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);

  const [searchId, setSearchId] = useState('');
  const [searchResult, setSearchResult] = useState(null);

  // ✅ FIXED: Add to CORRECT endpoint with FormData
  const handleAddSearchStudent = async (e) => {
    e.preventDefault();

    const fd = new FormData();
    fd.append('admission_no', admissionNo);
    fd.append('name', name);
    fd.append('branch', branch);
    fd.append('specialization', specialization);
    fd.append('phone', phone);
    fd.append('email', email);
    fd.append('dob', dob);
    if (photoFile) {
      fd.append('photo', photoFile);
    }

    try {
      // ✅ FIXED: Correct endpoint for ADDING
      const res = await fetch(`${API_BASE}/api/add_student`, {
        method: 'POST',
        body: fd  // FormData - NO Content-Type needed
      });
      const data = await res.json();
      if (res.ok && data.success) {
        window.alert('Student added successfully ✅');
        // Reset form
        setAdmissionNo('');
        setName('');
        setBranch('CSE');
        setSpecialization('');
        setPhone('');
        setEmail('');
        setDob('');
        setPhotoFile(null);
        setPhotoPreview(null);
      } else {
        window.alert(data.error || 'Error adding student');
      }
    } catch (err) {
      console.error(err);
      window.alert('Network error');
    }
  };

  // ✅ FIXED: Search with JSON POST
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchId.trim()) return;

    try {
      // ✅ FIXED: POST with JSON to correct endpoint
      const res = await fetch(`${API_BASE}/api/search_student`, {
        method: 'POST',  // Changed from GET
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          search_term: searchId.trim()
        })
      });
      const data = await res.json();
      setSearchResult(data);
      
      if (!data.success) {
        window.alert(data.error || 'Student not found');
      }
    } catch (err) {
      console.error(err);
      window.alert('Search error');
    }
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setPhotoFile(file);
    if (file) {
      const url = URL.createObjectURL(file);
      setPhotoPreview(url);
    } else {
      setPhotoPreview(null);
    }
  };

  const getPhotoUrl = (photoPath) => {
    if (!photoPath) return null;
    const parts = photoPath.split(/[\\/]/);
    const filename = parts[parts.length - 1];
    return `${API_BASE}/static_uploads/${filename}`;
  };

  return (
    <div>
      {/* Search box */}
      <div className="search-box">
        <input
          className="form-input search-input"
          placeholder="Enter Admission No to search..."
          value={searchId}
          onChange={(e) => setSearchId(e.target.value)}
        />
        <button className="search-btn" onClick={handleSearch}>
          Search
        </button>
      </div>

      {/* Search result */}
      {searchResult && searchResult.success && searchResult.student && (
        <div className="search-result-card">
          <div className="photo-wrapper">
            {searchResult.student.photo ? (
              <img
                src={getPhotoUrl(searchResult.student.photo)}
                alt={searchResult.student.name}
                className="student-photo"
              />
            ) : (
              <div className="student-photo placeholder">
                {searchResult.student.name.charAt(0)}
              </div>
            )}
          </div>

          <div className="result-header">
            <div>
              <h3>{searchResult.student.name}</h3>
              <p>
                {searchResult.student.admission_no} · {searchResult.student.branch}
              </p>
            </div>
          </div>

          <div className="detail-row">
            <span>Specialization</span>
            <span>{searchResult.student.specialization || '-'}</span>
          </div>
          <div className="detail-row">
            <span>Semester</span>
            <span>{searchResult.student.semester || '-'}</span>
          </div>
          <div className="detail-row">
            <span>Email</span>
            <span>{searchResult.student.email || '-'}</span>
          </div>
        </div>
      )}

      {searchResult && !searchResult.success && (
        <div className="empty-state">
          <i className="fas fa-search"></i>
          <p>{searchResult.error || 'No student found'}</p>
        </div>
      )}

      {/* Add student form */}
      <form className="form-grid" onSubmit={handleAddSearchStudent}>
        <input
          className="form-input"
          placeholder="Admission No *"
          value={admissionNo}
          onChange={(e) => setAdmissionNo(e.target.value)}
          required
        />
        <input
          className="form-input"
          placeholder="Full Name *"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <select
          className="form-select"
          value={branch}
          onChange={(e) => setBranch(e.target.value)}
        >
          <option value="CSE">CSE</option>
          <option value="AIML">AIML</option>
          <option value="AIDS">AIDS</option>
          <option value="ECE">ECE</option>
          <option value="MECH">MECH</option>
        </select>
        <input
          className="form-input"
          placeholder="Specialization"
          value={specialization}
          onChange={(e) => setSpecialization(e.target.value)}
        />
        <input
          className="form-input"
          placeholder="Phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
        <input
          className="form-input"
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          className="form-input"
          type="date"
          placeholder="Date of Birth"
          value={dob}
          onChange={(e) => setDob(e.target.value)}
        />

        <div className="file-upload">
          <label className="file-upload-label">
            Upload Photo
            <input
              type="file"
              accept="image/*"
              onChange={handlePhotoChange}
            />
          </label>
          {photoPreview && (
            <img src={photoPreview} alt="Preview" className="file-preview" />
          )}
        </div>

        <button type="submit" className="form-btn">
          ➕ Add Student
        </button>
      </form>
    </div>
  );
};

export default SearchStudent;
