import { useEffect, useState } from "react";

const API_BASE = "http://localhost:5000";

export default function ManageStudents() {
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({
    name: "",
    admission_no: "",
    branch: "",
    phone: "",
  });

  // ðŸ”¹ Load only manage students
  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    const res = await fetch(`${API_BASE}/api/students`);
    const data = await res.json();
    setStudents(data.students || []);
  };

  // ðŸ”¹ Add student (ONLY manage)
  const handleAdd = async () => {
    await fetch(`${API_BASE}/api/add_student`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    setForm({ name: "", admission_no: "", branch: "", phone: "" });
    fetchStudents(); // refresh ONLY manage list
  };

  // ðŸ”¹ Delete student (ONLY manage)
  const handleDelete = async (id) => {
    await fetch(`${API_BASE}/api/delete_student/${id}`, {
      method: "DELETE",
    });

    setStudents((prev) => prev.filter((s) => s.id !== id));
  };

  return (
    <div>
      <h2>Manage Students</h2>

      <input
        placeholder="Admission No"
        value={form.admission_no}
        onChange={(e) => setForm({ ...form, admission_no: e.target.value })}
      />
      <input
        placeholder="Name"
        value={form.name}
        onChange={(e) => setForm({ ...form, name: e.target.value })}
      />
      <input
        placeholder="Branch"
        value={form.branch}
        onChange={(e) => setForm({ ...form, branch: e.target.value })}
      />
      <input
        placeholder="Phone"
        value={form.phone}
        onChange={(e) => setForm({ ...form, phone: e.target.value })}
      />

      <button onClick={handleAdd}>Add Student</button>

      <hr />

      {students.map((s) => (
        <div key={s.id}>
          <b>{s.name}</b> ({s.admission_no})
          <button onClick={() => handleDelete(s.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}
