// src/components/AttendanceRecords.js
import React, { useState, useEffect } from "react";
import { API_BASE } from "../config";
import { getToken } from "../auth";

const AttendanceRecords = ({ branch, setBranch }) => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeBranch, setActiveBranch] = useState(branch);
  const [error, setError] = useState("");

  const branches = ["CSE", "AIML", "AIDS", "ECE", "MECH"];

  const loadTodayAttendance = async (selectedBranch) => {
    try {
      setLoading(true);
      console.log("üìã Loading attendance for:", selectedBranch);

      const token = getToken();
      const res = await fetch(
        `${API_BASE}/api/today_attendance/${selectedBranch}`,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: token ? `Bearer ${token}` : "",
          },
        }
      );
      const data = await res.json();
      console.log("üìã Records loaded:", data);

      if (!Array.isArray(data)) {
        setError(data.error || "Failed to load attendance");
        setRecords([]);
      } else {
        setError("");
        setRecords(data || []);
      }
      setActiveBranch(selectedBranch);
    } catch (error) {
      console.error("Load attendance failed:", error);
      setError("Network error loading attendance");
      setRecords([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadTodayAttendance(branch);
  }, [branch]);

  // ‚úÖ AUTO REFRESH every 10 seconds
  useEffect(() => {
    const interval = setInterval(
      () => loadTodayAttendance(activeBranch),
      10000
    );
    return () => clearInterval(interval);
  }, [activeBranch]);

  if (loading) {
    return (
      <div className="attendance-empty">
        <i className="fas fa-spinner fa-spin fa-2x"></i>
        <p>Loading attendance records...</p>
      </div>
    );
  }

  const safeRecords = Array.isArray(records) ? records : [];

  return (
    <div className="attendance-records">
      {/* optional inline error */}
      {error && (
        <p style={{ color: "red", marginBottom: "0.5rem" }}>{error}</p>
      )}

      {/* ‚úÖ BRANCH BUTTONS */}
      <div className="branch-selector">
        {branches.map((b) => (
          <button
            key={b}
            className={`branch-btn ${activeBranch === b ? "active" : ""}`}
            onClick={() => {
              setBranch(b);
              loadTodayAttendance(b);
            }}
          >
            {b}
            <span className="branch-badge">
              {safeRecords.filter((r) => r.branch === b).length}
            </span>
          </button>
        ))}
      </div>

      {/* ‚úÖ HEADER */}
      <div className="records-header">
        <h3>
          <i className="fas fa-calendar-day"></i>
          Today&apos;s Attendance - {activeBranch}
        </h3>
        <div className="header-actions">
          <span className="record-count">
            {safeRecords.length} marked today
          </span>
          <button
            className="refresh-btn"
            onClick={() => loadTodayAttendance(activeBranch)}
          >
            <i className="fas fa-sync-alt"></i> Refresh
          </button>
        </div>
      </div>

      {/* ‚úÖ RECORDS */}
      {safeRecords.length === 0 ? (
        <div className="attendance-empty">
          <i className="fas fa-users-slash fa-3x"></i>
          <h3>No attendance records today</h3>
          <p>Mark some attendance first to see records here</p>
          <button
            className="check-updates-btn"
            onClick={() => loadTodayAttendance(activeBranch)}
          >
            üîç Check for Updates
          </button>
        </div>
      ) : (
        <div className="records-list">
          {safeRecords.map((record, idx) => (
            <div key={idx} className="attendance-record">
              <div className="record-avatar">
                <i className="fas fa-user-circle"></i>
              </div>
              <div className="record-info">
                <h4>{record.name}</h4>
                <p>
                  <i className="fas fa-id-card"></i> {record.admission_no}
                </p>
                <p>
                  <i className="fas fa-clock"></i>{" "}
                  {new Date(record.timestamp).toLocaleTimeString()}
                </p>
              </div>
              <div className="record-status">
                <span className="status-present">
                  <i className="fas fa-check-circle"></i> Present
                </span>
                <div className="confidence-container">
                  <span className="confidence">{record.confidence}%</span>
                  <div className="confidence-bar">
                    <div
                      className="confidence-fill"
                      style={{
                        width: `${parseFloat(record.confidence)}%`,
                      }}
                    ></div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AttendanceRecords;
