import React, { useState, useEffect } from 'react';
import ManageStudents from './components/ManageStudents';
import SearchStudent from './components/SearchStudent';
// import EnrollFace from './components/EnrollFace' // your camera component

const App = () => {
  const [branch, setBranch] = useState('CSE');
  const [stats, setStats] = useState({ total: 0, today_present: 0 });
  const [activePage, setActivePage] = useState('manage'); // manage | enroll | search
  const [selectedStudent, setSelectedStudent] = useState(null);

  const loadStats = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/stats');
      const data = await res.json();
      setStats(data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    loadStats();
  }, []);

  const handleEnrollClick = (student) => {
    setSelectedStudent(student);
    setActivePage('enroll');
  };

  return (
    <div className="app">
      {/* sidebar etc... */}

      <div className="main-content">
        {activePage === 'manage' && (
          <ManageStudents
            branch={branch}
            setBranch={setBranch}
            onStatsUpdate={loadStats}
            onEnroll={handleEnrollClick}
          />
        )}

        {activePage === 'search' && <SearchStudent />}

        {/* {activePage === 'enroll' && selectedStudent && (
          <EnrollFace student={selectedStudent} />
        )} */}
      </div>
    </div>
  );
};

export default App;
