import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import StatsCard from './components/StatsCard';
import ManageStudents from './components/ManageStudents';
import AttendanceRecords from './components/AttendanceRecords';
import MarkAttendance from './components/MarkAttendance';
import SearchStudent from './components/SearchStudent';
import AdminPanel from './components/AdminPanel';
import './App.css';

function App() {
  const [activeSection, setActiveSection] = useState('manageStudents');
  const [stats, setStats] = useState({ total: 0, today_present: 0 });
  const [currentBranch, setCurrentBranch] = useState('CSE');

  // selected student (for enroll or mark attendance if needed)
  const [selectedStudent, setSelectedStudent] = useState(null);

  const fetchStats = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/stats');
      const data = await res.json();
      setStats(data);
    } catch (error) {
      console.log('Stats fetch failed:', error);
    }
  };

  useEffect(() => {
    fetchStats();
    const interval = setInterval(fetchStats, 30000); // Update every 30s
    return () => clearInterval(interval);
  }, []);

  // called when user clicks "Enroll Face" inside ManageStudents
  const handleEnrollClick = (student) => {
    setSelectedStudent(student);
    // IMPORTANT: do NOT change activeSection here
    // We stay on manageStudents; enrollment UI lives in that component
  };

  const getHeaderTitle = () => {
    if (activeSection === 'manageStudents') return 'üë• Manage Students';
    if (activeSection === 'attendanceRecords') return 'üìã Attendance Records';
    if (activeSection === 'markAttendance') return 'üé• Mark Attendance';
    if (activeSection === 'searchStudent') return 'üîç Search Student';
    if (activeSection === 'admin') return 'üëë Admin Panel';
    return '';
  };

  return (
    <div className="app">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h1>üìä Smart Attendance</h1>
        </div>
        <StatsCard
          title="Total Students"
          value={stats.total}
          icon="üë•"
          color="#4facfe"
        />
        <StatsCard
          title="Today's Present"
          value={stats.today_present}
          icon="‚úÖ"
          color="#00f2fe"
        />
        <Sidebar
          activeSection={activeSection}
          setActiveSection={setActiveSection}
          currentBranch={currentBranch}
          setCurrentBranch={setCurrentBranch}
        />
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="header">
          <h2>{getHeaderTitle()}</h2>
          <button
            className="admin-btn"
            onClick={() => setActiveSection('admin')}
          >
            <i className="fas fa-user-shield"></i> Admin
          </button>
        </div>

        {/* Sections */}
        {activeSection === 'manageStudents' && (
          <ManageStudents
            branch={currentBranch}
            setBranch={setCurrentBranch}
            onStatsUpdate={fetchStats}
            onEnroll={handleEnrollClick}
            selectedStudent={selectedStudent}
          />
        )}

        {activeSection === 'attendanceRecords' && (
          <AttendanceRecords
            branch={currentBranch}
            setBranch={setCurrentBranch}
          />
        )}

        {activeSection === 'markAttendance' && (
          <MarkAttendance onStatsUpdate={fetchStats} />
        )}

        {activeSection === 'searchStudent' && <SearchStudent />}

        {activeSection === 'admin' && <AdminPanel />}
      </div>
    </div>
  );
}

export default App;
