import React, { useEffect, useState } from "react";
import { API_BASE } from "../config";
import { getToken } from "../auth";

const AttendanceRecords = ({ branch, setBranch }) => {
  const [records, setRecords] = useState([]); // must be array
  const [error, setError] = useState("");

  const fetchRecords = async () => {
    setError("");
    try {
      const res = await fetch(`${API_BASE}/api/today_attendance/${branch}`, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${getToken()}`,
        },
      });
      const data = await res.json();

      // Ensure records is always an array
      if (Array.isArray(data)) {
        setRecords(data);
      } else if (Array.isArray(data.records)) {
        setRecords(data.records);
      } else {
        setRecords([]);
        setError(data.error || "No records");
      }
    } catch (e) {
      console.error("fetchRecords error:", e);
      setError("Failed to load attendance");
      setRecords([]);
    }
  };

  useEffect(() => {
    fetchRecords();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [branch]);

  const safeRecords = Array.isArray(records) ? records : [];

  return (
    <div>
      <h3>Attendance Records</h3>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Example filter usage guarded by Array.isArray */}
      {/* const presentOnly = safeRecords.filter(r => r.status === "Present"); */}

      <table
        border="1"
        cellPadding="6"
        style={{ marginTop: 10, width: "100%", borderCollapse: "collapse" }}
      >
        <thead>
          <tr>
            <th>Admission No</th>
            <th>Name</th>
            <th>Branch</th>
            <th>Date/Time</th>
            <th>Status</th>
            <th>Session</th>
            <th>Confidence</th>
          </tr>
        </thead>
        <tbody>
          {safeRecords.map((r, idx) => (
            <tr key={idx}>
              <td>{r.admission_no}</td>
              <td>{r.name}</td>
              <td>{r.branch}</td>
              <td>{r.timestamp}</td>
              <td>{r.status}</td>
              <td>{r.session}</td>
              <td>{r.confidence}</td>
            </tr>
          ))}
          {safeRecords.length === 0 && (
            <tr>
              <td colSpan="7" style={{ textAlign: "center" }}>
                No records
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceRecords;
