// src/components/ManageStudents.js
import React, { useState, useEffect, useRef, useCallback } from "react";

const API_BASE = "http://localhost:5000";

const ManageStudents = ({ branch, setBranch, onStatsUpdate }) => {
  const [students, setStudents] = useState([]);
  const [formData, setFormData] = useState({
    admissionNo: "",
    name: "",
    specialization: "",
    email: "",
    semester: "1",
  });

  const [enrollingStudent, setEnrollingStudent] = useState(null);
  const [isEnrolling, setIsEnrolling] = useState(false);
  const [isMarkingAttendance, setIsMarkingAttendance] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // ðŸ”„ LOAD STUDENTS
  const loadStudents = useCallback(async () => {
    try {
      const res = await fetch(`${API_BASE}/api/students/${branch}`);
      const data = await res.json();
      setStudents(data || []);
    } catch (err) {
      console.error("Load students error:", err);
    }
  }, [branch]);

  useEffect(() => {
    loadStudents();
  }, [loadStudents]);

  // âž• ADD STUDENT
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`${API_BASE}/api/add_student`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          admission_no: formData.admissionNo.trim(),
          name: formData.name.trim(),
          branch,
          specialization: formData.specialization.trim(),
          email: formData.email.trim(),
          semester: formData.semester,
        }),
      });

      const data = await res.json();

      if (res.ok && data.success) {
        alert("âœ… Student added");
        setFormData({
          admissionNo: "",
          name: "",
          specialization: "",
          email: "",
          semester: "1",
        });
        loadStudents();
        onStatsUpdate?.();
      } else {
        alert(data.error || "Add failed");
      }
    } catch {
      alert("âŒ Backend not running");
    }
  };

  // ðŸŽ¥ CAMERA CONTROLS
  const startCamera = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({
      video: { width: 320, height: 240 },
    });
    streamRef.current = stream;
    videoRef.current.srcObject = stream;
    await videoRef.current.play();
  };

  const stopCamera = () => {
    streamRef.current?.getTracks().forEach((t) => t.stop());
    streamRef.current = null;
    if (videoRef.current) videoRef.current.srcObject = null;
  };

  // ðŸŽ¯ ENROLL FACE
  const handleEnrollClick = async (student) => {
    setEnrollingStudent(student);
    setIsEnrolling(true);
    await startCamera();
  };

  const enrollFace = async () => {
    if (!enrollingStudent) return;

    const ctx = canvasRef.current.getContext("2d");
    ctx.drawImage(videoRef.current, 0, 0, 320, 240);
    const image = canvasRef.current.toDataURL("image/jpeg");

    try {
      const res = await fetch(`${API_BASE}/api/enroll_face`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          admission_no: enrollingStudent.admission_no,
          name: enrollingStudent.name,
          image,
        }),
      });

      const data = await res.json();

      if (data.success) {
        alert("âœ… Face enrolled");
        loadStudents();
      } else {
        alert(data.error);
      }
    } finally {
      setIsEnrolling(false);
      setEnrollingStudent(null);
      stopCamera();
    }
  };

  // ðŸŽ¯ MARK ATTENDANCE
  const handleMarkAttendanceClick = async () => {
    setIsMarkingAttendance(true);
    await startCamera();
  };

  const markAttendance = async () => {
    const ctx = canvasRef.current.getContext("2d");
    ctx.drawImage(videoRef.current, 0, 0, 320, 240);
    const image = canvasRef.current.toDataURL("image/jpeg");

    try {
      const res = await fetch(`${API_BASE}/api/mark_attendance`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ image, branch }),
      });

      const data = await res.json();
      alert(data.message || data.error);
      onStatsUpdate?.();
    } finally {
      setIsMarkingAttendance(false);
      stopCamera();
    }
  };

  const branches = ["CSE", "AIML", "AIDS", "ECE", "MECH"];

  return (
    <div>
      {/* BRANCH TABS */}
      <div className="branch-tabs">
        {branches.map((b) => (
          <button key={b} onClick={() => setBranch(b)}>
            {b}
          </button>
        ))}
      </div>

      {/* ADD FORM */}
      <form onSubmit={handleSubmit} className="form-grid">
        <input value={formData.admissionNo} onChange={(e) => setFormData({ ...formData, admissionNo: e.target.value })} placeholder="Admission No" required />
        <input value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} placeholder="Name" required />
        <input value={formData.specialization} onChange={(e) => setFormData({ ...formData, specialization: e.target.value })} placeholder="Specialization" />
        <input value={formData.email} onChange={(e) => setFormData({ ...formData, email: e.target.value })} placeholder="Email" />
        <button type="submit">âž• Add</button>
      </form>

      {/* ATTENDANCE */}
      <button onClick={handleMarkAttendanceClick}>
        ðŸŽ¥ Mark Attendance
      </button>

      {/* STUDENTS */}
      {students.map((s) => (
        <div key={s.admission_no}>
          <b>{s.name}</b> ({s.admission_no})
          <button onClick={() => handleEnrollClick(s)}>Enroll Face</button>
        </div>
      ))}

      {/* CAMERA */}
      {(isEnrolling || isMarkingAttendance) && (
        <div>
          <video ref={videoRef} />
          <canvas ref={canvasRef} width="320" height="240" hidden />
          {isEnrolling && <button onClick={enrollFace}>ðŸ“¸ Enroll</button>}
          {isMarkingAttendance && <button onClick={markAttendance}>âœ… Capture</button>}
        </div>
      )}
    </div>
  );
};

export default ManageStudents;
