import React, { useState } from 'react';

const API_BASE = 'http://localhost:5000';

const SearchStudent = () => {
  const [searchId, setSearchId] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [loading, setLoading] = useState(false);

  // üîç SEARCH ONLY
  const handleSearch = async () => {
    if (!searchId.trim()) return;

    setLoading(true);
    setSearchResult(null);

    try {
      const res = await fetch(`${API_BASE}/api/search_student`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ search_term: searchId.trim() }),
      });

      const data = await res.json();

      if (data.success) {
        setSearchResult(data);
      } else {
        setSearchResult({ success: false, error: data.error || 'Not found' });
      }
    } catch {
      setSearchResult({ success: false, error: 'Search failed' });
    } finally {
      setLoading(false);
    }
  };

  // üóëÔ∏è DELETE SEARCH STUDENT ONLY
  const handleDelete = async () => {
    if (!window.confirm('Delete this student?')) return;

    const admissionNo = searchResult.student.admission_no;

    const res = await fetch(`${API_BASE}/api/delete_search_student`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ admission_no: admissionNo }),
    });

    const data = await res.json();

    if (data.success) {
      alert('‚úÖ Deleted');
      setSearchResult(null);
      setSearchId('');
    } else {
      alert(data.error || 'Delete failed');
    }
  };

  return (
    <div className="search-student-container">
      <div className="search-box">
        <input
          placeholder="Admission No / Name"
          value={searchId}
          onChange={e => setSearchId(e.target.value)}
        />
        <button onClick={handleSearch}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </div>

      {searchResult?.success && (
        <div className="search-result-card">
          <h3>{searchResult.student.name}</h3>
          <p>{searchResult.student.admission_no}</p>
          <p>{searchResult.student.branch}</p>
          <button onClick={handleDelete}>üóëÔ∏è Delete</button>
        </div>
      )}

      {searchResult && !searchResult.success && (
        <p>‚ùå {searchResult.error}</p>
      )}
    </div>
  );
};

export default SearchStudent;
