import React, { useState } from 'react';

const SearchStudent = () => {
  const [activeTab, setActiveTab] = useState('add');
  const [searchAdmissionNo, setSearchAdmissionNo] = useState('');
  const [formData, setFormData] = useState({
    admissionNo: '',
    name: '',
    branch: 'CSE',
    specialization: '',
    phone: '',
    email: '',
    dob: ''
  });
  const [searchResult, setSearchResult] = useState(null);
  const [filePreview, setFilePreview] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFilePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const addStudent = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    Object.keys(formData).forEach(key => {
      formDataToSend.append(key, formData[key]);
    });
    
    try {
      const res = await fetch('http://localhost:5000/api/search_student', {
        method: 'POST',
        body: formDataToSend
      });
      if (res.ok) {
        alert('Student added successfully! ‚úÖ');
        setFormData({
          admissionNo: '',
          name: '',
          branch: 'CSE',
          specialization: '',
          phone: '',
          email: '',
          dob: ''
        });
        setFilePreview(null);
      }
    } catch (error) {
      alert('Error adding student!');
    }
  };

  const searchStudent = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/search_student/${searchAdmissionNo}`);
      const data = await res.json();
      setSearchResult(data.found ? data : null);
    } catch (error) {
      alert('Search failed!');
    }
  };

  const branches = ['CSE', 'AIML', 'AIDS', 'ECE', 'MECH'];

  return (
    <div>
      {/* Tabs */}
      <div className="search-tabs branch-tabs">
        <button 
          className={`tab-btn ${activeTab === 'add' ? 'active' : ''}`}
          onClick={() => setActiveTab('add')}
        >
          ‚ûï Add Student
        </button>
        <button 
          className={`tab-btn ${activeTab === 'search' ? 'active' : ''}`}
          onClick={() => setActiveTab('search')}
        >
          üîç Search Student
        </button>
      </div>

      {/* Add Student Form */}
      {activeTab === 'add' && (
        <form className="form-grid" onSubmit={addStudent}>
          <div className="file-upload">
            <input 
              type="file" 
              id="studentPhoto" 
              accept="image/*"
              onChange={handleFileChange}
            />
            {filePreview && (
              <img src={filePreview} alt="Preview" className="file-preview" />
            )}
          </div>
          <input
            className="form-input"
            placeholder="Admission No *"
            value={formData.admissionNo}
            onChange={(e) => setFormData({...formData, admissionNo: e.target.value})}
            required
          />
          <input
            className="form-input"
            placeholder="Full Name *"
            value={formData.name}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
            required
          />
          <select
            className="form-select"
            value={formData.branch}
            onChange={(e) => setFormData({...formData, branch: e.target.value})}
          >
            {branches.map(b => (
              <option key={b} value={b}>{b}</option>
            ))}
          </select>
          <input
            className="form-input"
            placeholder="Specialization"
            value={formData.specialization}
            onChange={(e) => setFormData({...formData, specialization: e.target.value})}
          />
          <input
            className="form-input"
            type="tel"
            placeholder="Phone Number"
            value={formData.phone}
            onChange={(e) => setFormData({...formData, phone: e.target.value})}
          />
          <input
            className="form-input"
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
          />
          <input
            className="form-input"
            type="date"
            value={formData.dob}
            onChange={(e) => setFormData({...formData, dob: e.target.value})}
            required
          />
          <button type="submit" className="form-btn">üíæ Save Student</button>
        </form>
      )}

      {/* Search Student */}
      {activeTab === 'search' && (
        <div className="search-section">
          <div className="search-box">
            <input
              className="form-input search-input"
              placeholder="Enter Admission Number"
              value={searchAdmissionNo}
              onChange={(e) => setSearchAdmissionNo(e.target.value)}
            />
            <button className="search-btn" onClick={searchStudent}>
              <i className="fas fa-search"></i> Search
            </button>
          </div>

          {searchResult && (
            <div className="search-result-card">
              <div className="result-header">
                <i className="fas fa-user-check"></i>
                <h3>Student Found!</h3>
              </div>
              <div className="result-details">
                <div className="detail-row">
                  <span>Name:</span>
                  <strong>{searchResult.name}</strong>
                </div>
                <div className="detail-row">
                  <span>ID:</span>
                  <strong>{searchResult.admission_no}</strong>
                </div>
                <div className="detail-row">
                  <span>Branch:</span>
                  <strong>{searchResult.branch} - {searchResult.specialization}</strong>
                </div>
                <div className="detail-row">
                  <span>Phone:</span>
                  <strong>{searchResult.phone}</strong>
                </div>
                <div className="detail-row">
                  <span>Email:</span>
                  <strong>{searchResult.email}</strong>
                </div>
                <div className="detail-row">
                  <span>DOB:</span>
                  <strong>{searchResult.dob}</strong>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchStudent;
