import React, { useState } from 'react';

const API_BASE = 'http://localhost:5000';

const SearchStudent = () => {
  const [admissionNo, setAdmissionNo] = useState('');
  const [name, setName] = useState('');
  const [fatherName, setFatherName] = useState('');
  const [village, setVillage] = useState('');
  const [branch, setBranch] = useState('CSE');
  const [specialization, setSpecialization] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [dob, setDob] = useState('');
  const [semester, setSemester] = useState('');

  const [searchId, setSearchId] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const buildPhotoUrl = (photoPath) => {
    if (!photoPath) return null;
    if (photoPath.startsWith('/static_uploads/')) {
      return `${API_BASE}${photoPath}`;
    }
    return `${API_BASE}/static_uploads/${photoPath}`;
  };

  // üîç SEARCH STUDENT
  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchId.trim()) {
      alert('Please enter admission number or name');
      return;
    }

    setLoading(true);
    setSearchResult(null);

    try {
      const res = await fetch(`${API_BASE}/api/search_student`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ search_term: searchId.trim() }),
      });

      const data = await res.json();

      if (!data.success) {
        setSearchResult(data); // ‚ùó keep failure state
        return;
      }

      setSearchResult(data);
    } catch (err) {
      console.error(err);
      setSearchResult({ success: false, error: 'Search failed' });
    } finally {
      setLoading(false);
    }
  };

  // üóëÔ∏è DELETE STUDENT (SEARCH TABLE)
  const handleDeleteStudent = async () => {
    if (!searchResult?.student?.admission_no) return;

    if (!window.confirm(`Delete ${searchResult.student.name}?`)) return;

    try {
      setLoading(true);

      const res = await fetch(`${API_BASE}/api/delete_search_student`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          admission_no: searchResult.student.admission_no,
        }),
      });

      const data = await res.json();

      if (res.ok && data.success) {
        alert('‚úÖ Search student deleted successfully!');
        setSearchResult(null);
        setSearchId('');
      } else {
        alert(data.error || 'Delete failed');
      }
    } catch (err) {
      console.error(err);
      alert('Delete error');
    } finally {
      setLoading(false);
    }
  };

  // ‚ûï ADD STUDENT
  const handleAddStudent = async (e) => {
    e.preventDefault();
    if (loading) return;

    setLoading(true);

    const fd = new FormData();
    fd.append('admission_no', admissionNo);
    fd.append('name', name);
    fd.append('father_name', fatherName);
    fd.append('village', village);
    fd.append('branch', branch);
    fd.append('specialization', specialization);
    fd.append('phone', phone);
    fd.append('email', email);
    fd.append('dob', dob);
    fd.append('semester', semester || '1');

    try {
      const res = await fetch(`${API_BASE}/api/add_student`, {
        method: 'POST',
        body: fd,
      });

      const data = await res.json();

      if (res.ok && data.success) {
        alert(`‚úÖ ${name} added successfully!`);
        setAdmissionNo('');
        setName('');
        setFatherName('');
        setVillage('');
        setBranch('CSE');
        setSpecialization('');
        setPhone('');
        setEmail('');
        setDob('');
        setSemester('');
      } else {
        alert(data.error || 'Error adding student');
      }
    } catch (err) {
      console.error(err);
      alert('Network error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="search-student-container">

      {/* üîç SEARCH BOX */}
      <div className="search-box">
        <input
          className="form-input search-input"
          placeholder="Enter Admission No / Name"
          value={searchId}
          onChange={(e) => setSearchId(e.target.value)}
          disabled={loading}
        />
        <button className="search-btn" onClick={handleSearch} disabled={loading}>
          {loading ? 'üîç Searching...' : 'üîç Search'}
        </button>
      </div>

      {/* ‚úÖ SEARCH SUCCESS */}
      {searchResult?.success && searchResult.student && (
        <div className="search-result-card">
          <div className="avatar-box">
            {searchResult.student.photo_path ? (
              <img
                src={buildPhotoUrl(searchResult.student.photo_path)}
                alt={searchResult.student.name}
              />
            ) : (
              <span>{searchResult.student.name?.charAt(0).toUpperCase()}</span>
            )}
          </div>

          <h3>{searchResult.student.name}</h3>
          <p>
            <strong>{searchResult.student.admission_no}</strong> ¬∑{' '}
            {searchResult.student.branch}
          </p>

          <div className="detail-grid">
            <p>Specialization: {searchResult.student.specialization || '-'}</p>
            <p>Semester: {searchResult.student.semester || '-'}</p>
            <p>Phone: {searchResult.student.phone || '-'}</p>
            <p>Email: {searchResult.student.email || '-'}</p>
          </div>

          <button className="delete-btn" onClick={handleDeleteStudent}>
            üóëÔ∏è Delete Student
          </button>
        </div>
      )}

      {/* ‚ùå SEARCH FAILURE */}
      {searchResult && !searchResult.success && (
        <div className="empty-state">
          <p>{searchResult.error || 'Student not found'}</p>
        </div>
      )}

      {/* ‚ûï ADD STUDENT */}
      <form className="form-grid" onSubmit={handleAddStudent}>
        <input placeholder="Admission No" value={admissionNo} onChange={(e) => setAdmissionNo(e.target.value)} required />
        <input placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <input placeholder="Father Name" value={fatherName} onChange={(e) => setFatherName(e.target.value)} />
        <input placeholder="Village" value={village} onChange={(e) => setVillage(e.target.value)} />
        <input placeholder="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} required />
        <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input placeholder="Semester" value={semester} onChange={(e) => setSemester(e.target.value)} required />
        <button type="submit" disabled={loading}>‚ûï Add Student</button>
      </form>
    </div>
  );
};

export default SearchStudent;
