import React, { useState, useEffect, useRef } from 'react';

const ManageStudents = ({ branch, setBranch, onStatsUpdate, onEnroll, selectedStudent }) => {
  const [students, setStudents] = useState([]);
  const [formData, setFormData] = useState({
    admissionNo: '',
    name: '',
    specialization: '',
    email: '',
    semester: '1'
  });

  const [enrollingStudent, setEnrollingStudent] = useState(null);
  const [isEnrolling, setIsEnrolling] = useState(false);
  const videoRef = useRef(null);

  const loadStudents = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/students/${branch}`);
      const data = await res.json();
      setStudents(data);
    } catch (error) {
      console.log('Load students failed:', error);
    }
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const res = await fetch('http://localhost:5000/api/add_student', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        admission_no: formData.admissionNo,
        name: formData.name,
        branch,
        specialization: formData.specialization,
        email: formData.email,
        semester: formData.semester
      })
    });

    let data = {};
    try {
      data = await res.json();
    } catch (err) {
      console.error('Response is not JSON', err);
    }

    console.log('Add student response:', res.status, data);

    if (res.ok && data.success) {
      window.alert('Student added successfully! ‚úÖ');
      setFormData({
        admissionNo: '',
        name: '',
        specialization: '',
        email: '',
        semester: '1'
      });
      loadStudents();
      onStatsUpdate();
    } else {
      window.alert(data.error || `Error adding student! (${res.status})`);
    }
  } catch (error) {
    console.error('Network / code error:', error);
    window.alert('Error adding student! (network)');
  }
};



  useEffect(() => {
    loadStudents();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [branch]);

  const deleteStudent = async (admissionNo) => {
    if (!window.confirm('Delete this student?')) {
      return;
    }
    try {
      const res = await fetch('http://localhost:5000/api/delete_student', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ admission_no: admissionNo })
      });
      const data = await res.json();
      if (res.ok && data.success) {
        window.alert('Student deleted successfully ‚úÖ');
        loadStudents();
        onStatsUpdate();
      } else {
        window.alert(data.error || 'Error deleting student!');
        console.error('Delete error:', data);
      }
    } catch (error) {
      window.alert('Error deleting student!');
      console.error(error);
    }
  };

  // CAMERA HANDLING FOR ENROLL
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
      }
    } catch (err) {
      console.error(err);
      window.alert('Cannot access camera');
    }
  };

  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach((t) => t.stop());
      videoRef.current.srcObject = null;
    }
  };

  const handleEnrollClickLocal = (student) => {
    setEnrollingStudent(student);
    if (onEnroll) onEnroll(student);
    setIsEnrolling(true);
    startCamera();
  };

  // This calls backend /api/enroll_face, which itself captures 200 images
  const enrollFace = async () => {
    if (!enrollingStudent) return;

    try {
      const res = await fetch('http://localhost:5000/api/enroll_face', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          admission_no: enrollingStudent.admission_no,
          name: enrollingStudent.name
        })
      });

      const data = await res.json();
      if (res.ok && data.success) {
        window.alert(data.message || 'Face enrolled successfully!');
      } else {
        window.alert(data.error || 'Error enrolling face');
      }
    } catch (err) {
      console.error(err);
      window.alert('Error enrolling face');
    } finally {
      setIsEnrolling(false);
      stopCamera();
    }
  };

  const branches = ['CSE', 'AIML', 'AIDS', 'ECE', 'MECH'];

  return (
    <div>
      <div className="branch-tabs">
        {branches.map((b) => (
          <button
            key={b}
            className={`tab-btn ${branch === b ? 'active' : ''}`}
            onClick={() => setBranch(b)}
          >
            {b}
          </button>
        ))}
      </div>

      <form className="form-grid" onSubmit={handleSubmit}>
        <input
          className="form-input"
          placeholder="Admission No *"
          value={formData.admissionNo}
          onChange={(e) =>
            setFormData({ ...formData, admissionNo: e.target.value })
          }
          required
        />
        <input
          className="form-input"
          placeholder="Student Name *"
          value={formData.name}
          onChange={(e) =>
            setFormData({ ...formData, name: e.target.value })
          }
          required
        />
        <input
          className="form-input"
          placeholder="Specialization"
          value={formData.specialization}
          onChange={(e) =>
            setFormData({ ...formData, specialization: e.target.value })
          }
        />
        <input
          className="form-input"
          type="email"
          placeholder="Email"
          value={formData.email}
          onChange={(e) =>
            setFormData({ ...formData, email: e.target.value })
          }
        />
        <select
          className="form-select"
          value={formData.semester}
          onChange={(e) =>
            setFormData({ ...formData, semester: e.target.value })
          }
        >
          {[1, 2, 3, 4, 5, 6, 7, 8].map((s) => (
            <option key={s} value={s}>
              Semester {s}
            </option>
          ))}
        </select>
        <button type="submit" className="form-btn">
          ‚ûï Add Student
        </button>
      </form>

      <div>
        {students.map((student) => (
          <div key={student.admission_no} className="student-item">
            <div>
              <h3>{student.name}</h3>
              <p>ID: {student.admission_no} | {branch}</p>
            </div>
            <div className="student-actions">
              <button
                className="btn-small btn-enroll"
                onClick={() => handleEnrollClickLocal(student)}
              >
                Enroll Face
              </button>
              <button
                className="btn-small btn-delete"
                onClick={() => deleteStudent(student.admission_no)}
              >
                üóëÔ∏è Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Enrollment camera area, stays on Manage Students */}
      {isEnrolling && (
        <div className="camera-container">
          <video id="video" ref={videoRef}></video>
          <button className="capture-btn" onClick={enrollFace}>
            Start 200‚Äësample Enrollment
          </button>
        </div>
      )}
    </div>
  );
};

export default ManageStudents;
